import React, { useState, useEffect } from 'react';
import { APIProvider } from '@vis.gl/react-google-maps';
import axios from 'axios';

const OrderForm = ({ token }) => {
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [serviceType, setServiceType] = useState('taxi');
  const [error, setError] = useState('');

  useEffect(() => {
    if (window.google) {
      const startInput = document.getElementById('startLocation');
      const endInput = document.getElementById('endLocation');
      const startAutocomplete = new window.google.maps.places.Autocomplete(startInput);
      const endAutocomplete = new window.google.maps.places.Autocomplete(endInput);

      startAutocomplete.addListener('place_changed', () => {
        const place = startAutocomplete.getPlace();
        setStartLocation(place.formatted_address);
      });

      endAutocomplete.addListener('place_changed', () => {
        const place = endAutocomplete.getPlace();
        setEndLocation(place.formatted_address);
      });
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/orders', {
        service_type: serviceType,
        start_location: startLocation,
        end_location: endLocation
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('Order placed:', response.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Ошибка при создании заказа');
      console.error(err);
    }
  };

  return (
    <APIProvider apiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <div className="max-w-md mx-auto p-4 bg-white rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-4">Разместить заказ</h2>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div>
            <label className="block mb-2">Тип услуги</label>
            <select
              value={serviceType}
              onChange={(e) => setServiceType(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="taxi">Такси</option>
              <option value="food_delivery">Доставка еды</option>
              <option value="other">Другое</option>
            </select>
          </div>
          <div>
            <label className="block mb-2">Откуда</label>
            <input
              id="startLocation"
              type="text"
              placeholder="Начальная точка"
              value={startLocation}
              onChange={(e) => setStartLocation(e.target.value)}
              className="w-full p-2 border rounded"
            />
          </div>
          <div>
            <label className="block mb-2">Куда</label>
            <input
              id="endLocation"
              type="text"
              placeholder="Конечная точка"
              value={endLocation}
              onChange={(e) => setEndLocation(e.target.value)}
              className="w-full p-2 border rounded"
            />
          </div>
          <button
            type="submit"
            className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Разместить заказ
          </button>
        </form>
      </div>
    </APIProvider>
  );
};

export default OrderForm;
